  // const handleSubmit = (e) => {
  //   setForm({ ...form, id: form.id + 1 });
  //   e.preventDefault();
  //   register();
  //   console.log(form);
  //   // console.log({id, name,email, username, password})
  // }
/*
  const [user, setUser] = useState({
    id:0,
    name:'',
    username:'',
    email:'',
    password:''

  });

  const [updatedUser, setUpdatedUser] = useState({
    name: '',
    email: '',
    username: '',
    password: ''
});

  const [isEditing, setIsEditing] = useState(false);

  const handleRetrieveUser = () => {
    // Replace {id} with the actual user ID  
    axios.get('http://localhost:8080/api/users/{id}')
        .then(response => {
            setUser(response.data);
        })
        .catch(error => {
            console.log(error);
        });
};

const handleUpdateUser = () => {
    // Replace {id} with the actual user ID
    axios.put(`http://localhost:8080/api/users/{id}`, user)
        .then(response => {
            setUser(response.data);
            setIsEditing(false);
        })
        .catch(error => {
            console.log(error);
        });
};



const handleDeleteUser = () => {
    // Replace {id} with the actual user ID
    axios.delete(`http://localhost:8080/api/users/{id}`)
        .then(() => {
            // User deleted, you can redirect or update the UI accordingly
        })
        .catch(error => {
            console.log(error);
        });
};

useEffect(() => {
    // Retrieve user details when the component loads
    handleRetrieveUser();
}, []);

  
  return (
    <div>
      <section className="vh-100" style={{ backgroundColor: "#eee" }}>
        <div className="container h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-lg-12 col-xl-11">
              <div className="card text-black" style={{ borderRadius: "25px" }}>
                <div className="card-body p-md-5">
                  <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                    <h3 className="fw-normal mb-3 pb-3" style={{ letterSpacing: "1px" }}>View Profile</h3>
                    <form className="mx-1 mx-md-4" >
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-user fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example1c">Your Name</label>
      <input
        type="text"
        name='name'
        value={user.name}
        onChange={(e) => setUpdatedUser({ ...updatedUser, name: e.target.value })}
        id="form3Example1c"
        className="form-control"
        // placeholder="Enter your name"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-envelope fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example3c">Your Email</label>
      <input
        type="email"
        name='email'
        value={user.email}
        onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })}
        id="form3Example3c"
        className="form-control"
        // placeholder="Enter your email"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-key fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example4cd">Username</label>
      <input
        type="text"
        name='username'
        value={user.username}
        onChange={(e) => setUpdatedUser({ ...updatedUser, username: e.target.value })}
        id="form3Example4cd"
        className="form-control"
        // placeholder="Choose a username"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-lock fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example4c">Password</label>
      <input
         type="text"
         name="password"
         value={user.password}
         onChange={(e) => setUpdatedUser({ ...updatedUser, password: e.target.value })}
        // id="form3Example4c"
        className="form-control"
        
      />
    </div>
  </div>


  <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
    <button type='submit' onClick={handleUpdateUser} className="btn  btn-lg btn-block" style={{ backgroundColor: 'purple', color: 'white', marginRight: '15px'}}>Update</button>
    <button id="cancel" name="cancel" onClick={handleDeleteUser} className="btn btn-default purple-border" value="1"
            style={{  width: "100px" }}
        >
         Delete
        </button>
  </div>
</form>

                    </div>
                    <div className="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2 signUp-bg">
                     </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
    */

    const [formData, setFormData] = useState({
      name: user.name,
      username: user.username,
      email: user.email,
      city: user.city,
      address: user.address,
      phoneNumber: user.phoneNumber,
      password: user.password,
    });
  
    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    const handleRegistration = async (e) => {
      e.preventDefault();
  console.log(formData);
      try {
        await axios.put(`http://localhost:8080/api/v1/user/${user.username}`, formData);
        alert("Save successful!");
      } catch (error) {
        alert("Save failed. Please try again.");
        console.error("Error updating user data:", error);
      }
    };
    return (
      <div className="flex flex-col mb-20 items-center bg-cyan-50 rounded">
        <div className="text-2xl font-bold m-5 text-slate-800">Register</div>
        <div className="flex flex-col items-center">
          <form
            className="flex flex-col items-center"
            onSubmit={handleRegistration}
          >
            <div className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80 ">
              Username: {formData.username}
            </div>
            <input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="text"
              placeholder="Name"
            />
  
            <input
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="text"
              placeholder="Email"
            />
            <input
              name="city"
              value={formData.city}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="text"
              placeholder="City"
            />
            <input
              name="address"
              value={formData.address}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="text"
              placeholder="Address"
            />
            <input
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="text"
              placeholder="Phone Number"
            />
            <input
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              className="border-2 border-gray-400 rounded-lg m-2 p-2 w-80"
              type="password"
              placeholder="Password"
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg w-80"
            >
              Save
            </button>
          </form>
        </div>
      </div>

  );
}
