import React, { useState } from 'react';
import axios from 'axios';

const user = {
  username: '',
  password: ''
}

export default function SignIn() {
  const [form, setForm] = useState({
    username: '',
    password: ''
  });

  const login = (username, password) => async (dispatch) => {
    try {
      const response = await axios.post('/api/login', { username, password });
      // Handle the response from the server, such as updating user authentication state.
      // Dispatch appropriate actions based on success or failure.
    } catch (error) {
      // Handle login error, e.g., dispatch an error action.
    }
  };

  const { username, password } = form;

  const handleChange = (e) => {
    setFormData({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    login(username, password);
  };

  return (
    <section className="vh-100" style={{ backgroundColor: "#eee" }}>
      <div className="container h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-lg-12 col-xl-11">
            <div className="card text-black" style={{ borderRadius: "25px" }}>
              <div className="card-body p-md-5">
                <div className="row justify-content-center">
                  <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                    <form onSubmit={handleSubmit}>
                      <span className="h1 fw-bold mb-0 signIn_Font">Login into your  VIA account</span>
                      <h3 className="fw-normal mb-3 pb-3" style={{ letterSpacing: "1px" }}>Sign in</h3>

                      <div className="form-outline mb-4">
                        <input onChange={handleChange} type="email" id="form2Example18" className="form-control form-control-lg" />
                        <label className="form-label" htmlFor="form2Example18">VIA Account</label>
                      </div>

                      <div className="form-outline mb-4">
                        <input onChange={handleChange} type="password" id="form2Example28" className="form-control form-control-lg" />
                        <label className="form-label" htmlFor="form2Example28">Password</label>
                      </div>

                      <div className="pt-1 mb-4">
                        <button className="btn btn-info btn-lg btn-block" type="button" style={{ backgroundColor: 'purple', color: 'white' }}>Login</button>
                      </div>

                      <p className="small mb-5 pb-lg-2"><a className="text-muted" href="#!">Forgot password?</a></p>
                      <p>Don't have an account? <a href="#!" className="link-info">Register here</a></p>
                    </form>
                  </div>
                  <div className="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2 signIn-bg">
                    {/* <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
                        className="img-fluid"
                        alt="Sample image"
                      /> */}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
