import React , {useState} from 'react';
import axios from 'axios';
import { Link, useLocation, useNavigate  } from 'react-router-dom';
import {NotificationContainer, NotificationManager} from 'react-notifications';

export default function SignUp() {
  // const [form, setForm] = useState({
  //   id: 7,
  //   name: '',
  //   email: '',
  //   username: '',
  //   password: ''
  // });const { id,email,name, username, password } = form;



  // const register = () =>  {
  //   axios.post('http://localhost:8080/users', form )
  //   .then(response => {
  //     console.log(response);
  //   })
  //    .catch(error => {
  //     console.log(error);
  //   });   
  // }

  

  // const handleChange = (e) => {
  //   setForm({ ...form, [e.target.name]: e.target.value });
  // }

  // const handleSubmit = (e) => {
  //   setForm({ ...form, id: form.id + 1 });
  //   e.preventDefault();
  //   register();
  //   console.log(form);
  //   // console.log({id, name,email, username, password})
  // }

  const [user, setUser] = useState({});
  const [updatedUser, setUpdatedUser] = useState({});

  
  useEffect(() => {
    // Retrieve user details when the component mounts
    axios.get('/api/users/{id}') // Replace {id} with the actual user ID
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user details', error);
      });
  }, []);

  const handleUpdate = () => {
    // Send a PUT request to update user details
    axios.put('/api/users/{id}', updatedUser)
      .then((response) => {
        console.log('User details updated', response.data);
      })
      .catch((error) => {
        console.error('Error updating user details', error);
      });
  };

  const handleDelete = () => {
    // Send a DELETE request to delete the user
    axios.delete('/api/users/{id}')
      .then(() => {
        console.log('User deleted');
        // Redirect or perform other actions after deletion
      })
      .catch((error) => {
        console.error('Error deleting user', error);
      });
  };

  return (
    <div>
      <section className="vh-100" style={{ backgroundColor: "#eee" }}>
        <div className="container h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-lg-12 col-xl-11">
              <div className="card text-black" style={{ borderRadius: "25px" }}>
                <div className="card-body p-md-5">
                  <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                    <h3 className="fw-normal mb-3 pb-3" style={{ letterSpacing: "1px" }}>View Profile</h3>
                    <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-user fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example1c">Your Name</label>
      <input
        type="text"
        name='name'
        value={updatedUser.name}
        onChange={(e) => setUpdatedUser({ ...updatedUser, name: e})}
        id="form3Example1c"
        className="form-control"
        placeholder="Enter your name"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-envelope fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example3c">Your Email</label>
      <input
        type="email"
        name='email'
        value={email}
        onChange={handleChange}
        id="form3Example3c"
        className="form-control"
        placeholder="Enter your email"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-key fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example4cd">Username</label>
      <input
        type="text"
        name='username'
        value={username}
        onChange={handleChange}
        id="form3Example4cd"
        className="form-control"
        placeholder="Choose a username"
      />
    </div>
  </div>
  <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-lock fa-lg me-3 fa-fw"></i>
    <div className="form-outline flex-fill mb-0">
      <label className="form-label" htmlFor="form3Example4c">Password</label>
      <input
        type="password"
        name='password'
        value={password}
        onChange={handleChange}
        id="form3Example4c"
        className="form-control"
        placeholder="Enter your password"
      />
    </div>
  </div>


  <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
    <button type='submit' className="btn  btn-lg btn-block" style={{ backgroundColor: 'purple', color: 'white', marginRight: '15px'}}>Update</button>
    <button id="cancel" name="cancel" className="btn btn-default purple-border" value="1"
            style={{  width: "100px" }}
        >
         Delete
        </button>
  </div>
</form>

                    </div>
                    <div className="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2 signUp-bg">
                     </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
