import React, { useState } from 'react';
import axios from 'axios';

export default function SignUp() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
  });

  const [isEmailValid, setIsEmailValid] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  const handleEmailBlur = () => {
    // Check if the email is valid (you can implement your own validation logic)
    if (form.email) {
      setIsEmailValid(true);
    }
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEmailValid) {
      // If the email is valid, you can make an API request or perform other actions
      axios.post('http://localhost:8080/users', form)
        .then(response => {
          console.log(response);
        })
        .catch(error => {
          console.log(error);
        });
    } else {
      alert('Please enter a valid email first.');
    }
  }

  return (
    <div>
      <section className="vh-100" style={{ backgroundColor: "#eee" }}>
        <div className="container h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-lg-12 col-xl-11">
              <div className="card text-black" style={{ borderRadius: "25px" }}>
                <div className="card-body p-md-5">
                  <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                      <h3 className="fw-normal mb-3 pb-3" style={{ letterSpacing: "1px" }}>Registration Form</h3>
                      <form className="mx-1 mx-md-4" onSubmit={handleSubmit}>
                        <div className="d-flex flex-row align-items-center mb-4">
                          <i className="fas fa-envelope fa-lg me-3 fa-fw"></i>
                          <div className="form-outline flex-fill mb-0">
                            <label className="form-label" htmlFor="formEmail">Your Email</label>
                            <input
                              type="email"
                              name="email"
                              value={form.email}
                              onChange={handleChange}
                              onBlur={handleEmailBlur}
                              id="formEmail"
                              className="form-control"
                              placeholder="Enter your email"
                            />
                          </div>
                        </div>
                        {isEmailValid && (
                          <>
                            <div className="d-flex flex-row align-items-center mb-4">
                              <i className="fas fa-user fa-lg me-3 fa-fw"></i>
                              <div className="form-outline flex-fill mb-0">
                                <label className="form-label" htmlFor="formName">Your Name</label>
                                <input
                                  type="text"
                                  name="name"
                                  value={form.name}
                                  onChange={handleChange}
                                  id="formName"
                                  className="form-control"
                                  placeholder="Enter your name"
                                />
                              </div>
                            </div>
                            <div className="d-flex flex-row align-items-center mb-4">
                              <i className="fas fa-key fa-lg me-3 fa-fw"></i>
                              <div className="form-outline flex-fill mb-0">
                                <label className="form-label" htmlFor="formUsername">Username</label>
                                <input
                                  type="text"
                                  name="username"
                                  value={form.username}
                                  onChange={handleChange}
                                  id="formUsername"
                                  className="form-control"
                                  placeholder="Choose a username"
                                />
                              </div>
                            </div>
                            <div className="d-flex flex-row align-items-center mb-4">
                              <i className="fas fa-lock fa-lg me-3 fa-fw"></i>
                              <div className="form-outline flex-fill mb-0">
                                <label className="form-label" htmlFor="formPassword">Password</label>
                                <input
                                  type="password"
                                  name="password"
                                  value={form.password}
                                  onChange={handleChange}
                                  id="formPassword"
                                  className="form-control"
                                  placeholder="Enter your password"
                                />
                              </div>
                            </div>
                          </>
                        )}
                        <div className="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                          <button type="submit" className="btn btn-lg btn-block" style={{ backgroundColor: 'purple', color: 'white', marginRight: '15px' }}>Register</button>
                        </div>
                      </form>
                    </div>
                    <div className="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2 signUp-bg">
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}
